import { mapComponentThemes } from "../themes";
import { choiceTheme, choiceGroupTheme } from "../Choice"; // $FlowFixMe - strict theme keys incompatible with mapComponentThemes

export var radioTheme = function radioTheme(baseTheme) {
  return mapComponentThemes({
    name: 'Choice',
    theme: choiceTheme(baseTheme)
  }, {
    name: 'Radio',
    theme: {
      RadioControl_borderRadius: '100%'
    }
  }, baseTheme);
}; // $FlowFixMe - strict theme keys incompatible with mapComponentThemes

export var radioGroupTheme = function radioGroupTheme(baseTheme) {
  return mapComponentThemes({
    name: 'ChoiceGroup',
    theme: choiceGroupTheme(baseTheme)
  }, {
    name: 'RadioGroup',
    theme: {}
  }, baseTheme);
};